let onEnterCallback = null;

let onEnter = function() {
	if (onEnterCallback) {
		if (onEnterCallback.isFunction) onEnterCallback();
		else if (onEnterCallback.isArray)
			onEnterCallback[1].call(onEnterCallback[0]);
	} 
