let onEnterCallback = null;

let onEnter = function() {
	if (onEnterCallback) {
		if (onEnterCallback.isFunction) onEnterCallback();
		else if (onEnterCallback.isArray)
			onEnterCallback[1].call(onEnterCallback[0]);
	} 
	Snippet.widget.close();
};

let onEscape = function() {
	Snippet.widget.close();
};

Snippet.widget.open = function(message, callback) {
	this.childrenByKeys.stream.childrenByKeys.message.text(message);
	this.childrenByKeys.stream.childrenByKeys.message.height(
		this.childrenByKeys.stream.childrenByKeys.message.childrenByKeys.text.height('px') + 10 + 'px'
	);

	var top = (this.height('px') - this.childrenByKeys.stream.height('px')) * 0.5;
	if (top < 0) top = 0;
	this.childrenByKeys.stream.top(top + 'px');

	this.show();
	onEnterCallback = callback;
};

Snippet.widget.close = function() {
	this.hide();
	onEnterCallback = null;
};

Snippet.find('yes').click(onEnter);
Snippet.find('no').click(()=> Snippet.widget.close());
